        </div>
        <!-- [formGroup]="deliveryhero" -->
        <form class="row g-12" action="" (ngSubmit)="addHero()" >
            <div class="col-md-6 for">
              <label for="inputName5" class="form-label">Your Name</label>
              <input type="text" formControlName="heroName" [(ngModel)]="deliveryhero.heroName" class="form-control" id="inputName5">
            </div>
            <div class="col-md-6">
              <label for="inputEmail5" class="form-label">Email</label>
              <input type="email" formControlName="heroEmail" [(ngModel)]="deliveryhero.heroEmail" class="form-control" id="inputEmail5">
            </div>
            <div class="col-md-6">
              <label for="inputPassword5" class="form-label">Phone Number</label>
              <input type="text" formControlName="heroCell" [(ngModel)]="deliveryhero.heroCell" class="form-control"  heroCell id="inputCell5">
            </div>
            <div class="col-md-6">
              <label for="inputAddress5" class="form-label">Address</label>
