            <div class="text-center mt-2">
              <button type="submit" class="btn btn-outline-success m-2">Submit</button>
              <button  type="reset" class="btn btn-outline-secondary m-2">Reset</button>
            </div>
          </form>
    </div>
    
    <div class="border border-info p-4 mt-2" >
        <form class="row g-12" action="">
