          </div>
          <div class="col-md-1 text-center">
            <label for="inputName5" class="label label-primary "><Strong>Zip</Strong></label>
            <label for="inputName5" class="form-label "></label>
          </div>
    
          <div class="col-md-2 text-center">
            <label class="label label-primary"><Strong>Delete/Edit</Strong></label>
            <a type="submit" class="form-label "></a>
            <a type="submit" class="form-label "></a>
          </div>
        </form>
    </div>
    
    <div class="border border-info p-4 mt-2">
        <table class="table table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">1</th>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
              </tr>
            </tbody>
          </table>
    </div>
    </div>
    </main>
